package demo_operators;

public class Bitwise {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		 int x = 9;
	        int y = 8;
	        int And = x & y;
	        int Or = x | y;
	        int Xor = x ^ y;

	        System.out.println("x & y = " + And);
	        System.out.println("x | y = " + Or);
	        System.out.println("x ^ y = " + Xor);

	}

}
